// Componente Raíz del proyecto
import React from "react";
import { BrowserRouter as Router, Route, Link } from 'react-router-dom'
import logo from '../logo.svg';
import './App.css';
import CrearUsuario from './CrearUsuario'
import ListarUsuarios from './ListarUsuarios'

function App(){
    let estiloLogo = { // Objeto de JS  con propiedades CSS
        width: "5em",
        height: "5em"
    }
    return (
        <Router>
            <div className="App">
                <header className="App-header">
                    <img src={ logo } style={ estiloLogo } className="App-logo"  alt="logo"/>
                    <h1>Operaciones CRUD usuarios</h1>
                </header>
                <nav>
                    <Link to="/">Listado</Link>
                    <Link to="/registro">Registro</Link>
                </nav>
                <Route path="/" exact component={ ListarUsuarios }/>
                <Route path="/registro" component={ CrearUsuario }/>
            </div>
        </Router>
    );
}
export default App;
