import React, { Component } from 'react'
import { BrowserRouter as Router, Route, Link } from 'react-router-dom'
import EditarUsuario from "./EditarUsuario";


class ListarUsuarios extends /*React.*/ Component{
    // equivalente al ngOnInit
    componentDidMount(){
        this.setState (null);
        let promesaHTTP = window.fetch(`http://127.0.0.1:4000/api/usuarios/`)
        promesaHTTP.then((resHTTP)=>{    
            let promesaJSON = resHTTP.json()
            promesaJSON.then((objColeccionUsu)=>{
                console.log(JSON.stringify(objColeccionUsu))
                // Cuando por fin recibimos la colección y ha sido convertida en JSON
                this.setState({
                    listaUsuarios: objColeccionUsu
                })
            })
        })
    }

    componentWillUnmount(){
        // Aquí se ejectua cuando se desmonte el componente
    }

    // Si this.state no existe, mostramos "Cargando..."
    render(){

        let objViDomJSX;
        if(this.state === null){
            objViDomJSX = (<p>Cargando...</p>)
        } else{

            let botonEditar = <div>
                {/*<Link to="/editar">Editar</Link>*/}
                <input type="button" value="Edit" onClick="console.log(usu._id)"/>
            </div>
            let filasTr = this.state.listaUsuarios.map((usu)=>{
                let botonEliminar = function handleClick(e) {
                    e.preventDefault();
                    console.log(usu._id);
                    fetch('http://127.0.0.1:4000/api/usuarios/', {
                        method: 'delete',
                        body: JSON.stringify({
                            "id": usu._id,
                        }),
                        headers:{
                            'Content-Type': 'application/json'
                        }
                    }).then((res)=> alert("Eliminación completa")).catch((vacas)=> "MAL MAL MAL MAL MAL")
                  }
                return( <tr key={ usu._id }>
                            <td>{usu.nombre}</td>
                            <td>{usu.email}</td>
                            <td>{ usu.edad }</td>
                            {/*<td>{usu.password}</td>*/}
                            {/*<td>{botonEditar}</td>*/}
                            <td><button onClick={botonEliminar}>Edit</button>
                                <button onClick= {botonEliminar} >Borrar</button>
                            </td>
                        </tr>)
            })

            objViDomJSX = (
                <div>
                    <h2>Lista de usuarios</h2>
                    <table>
                        <thead>
                            <tr>
                                <th>Nombre</th>
                                <th>Email</th>
                                <th>Edad</th>
                                {/*<th>Password</th>*/}
                            </tr>
                        </thead>
                        <tbody>
                            {filasTr}
                        </tbody>
                    </table>
                </div>
            )
        }

        return objViDomJSX
    }
}
export default ListarUsuarios;
