import React, { Component } from 'react'
import { BrowserRouter as Router, Route, Link } from 'react-router-dom'

class ListarUsuarios extends /*React.*/ Component{
    // equivalente al ngOnInit
    componentDidMount(){
        this.setState (null);
        let promesaHTTP = window.fetch(`http://127.0.0.1:4000/api/usuarios/`)
        promesaHTTP.then((resHTTP)=>{    
            let promesaJSON = resHTTP.json()
            promesaJSON.then((objColeccionUsu)=>{
                console.log(JSON.stringify(objColeccionUsu))
                // Cuando por fin recibimos la colección y ha sido convertida en JSON
                this.setState({
                    listaUsuarios: objColeccionUsu
                })
            })
        })
    }
    componentWillUnmount(){
        // Aquí se ejectua cuando se desmonte el componente
    }
    // Si this.state no existe, mostramos "Cargando..."
    render(){

        let objViDomJSX;
        if(this.state === null){
            objViDomJSX = (<p>Cargando...</p>)
        } else{
            let botonEditar = <div>
               <div>
                <h2>Modificar datos</h2>
                <form onSubmit={ this.onSubmit }>
                    <div>
                        <label>Nombre: </label>
                        <input type="text" placeholder="Gilberto" 
                                value={ this.state.nombre } 
                                onChange={ this.onChangeNombre }/>
                    </div>
                    <div>
                        <label>Email: </label>
                        <input type="email" placeholder="ejemplo@gmail.com" 
                                value={ this.state.email } 
                                onChange={ this.onChangeEmail }/>
                    </div>
                    <div>
                        <label>Password: </label>
                        <input type="password" placeholder="l3tr4s y números" 
                                value={ this.state.password }
                                onChange={ this.onChangePassword }/>
                    </div>
                    <div>
                        <label>Edad: </label>
                        <input type="text" placeholder="33" 
                                value={ this.state.edad }
                                onChange={ this.onChangeEdad }/>
                    </div>
                    <div>
                        <input type="submit" value="Enviar"/>
                    </div>
                </form>
            </div>
            </div>
            let filasTr = this.state.listaUsuarios.map((usu)=>{

                return( <tr key={ usu._id }>
                            <td>{usu.nombre}</td>
                            <td>{usu.email}</td>
                            <td>{ usu.edad }</td>
                            {/*<td>{usu.password}</td>*/}
                            <td>{botonEditar}</td>
                        </tr>)
            })

            objViDomJSX = (
                <div>
                    <h2>Lista de usuarios</h2>
                    <table>
                        <thead>
                            <tr>
                                <th>Nombre</th>
                                <th>Email</th>
                                <th>Edad</th>
                                {/*<th>Password</th>*/}
                            </tr>
                        </thead>
                        <tbody>
                            {filasTr}
                        </tbody>
                    </table>
                </div>
            )
        }

        return objViDomJSX
    }
}
export default ListarUsuarios;
